local P,S=game:GetService("Players"),game:GetService("RunService")local U=game:GetService("UserInputService")local L=game:GetService("LogService")local C=workspace.CurrentCamera local F=200 local A=1 local B="Head" local b={"Head","Neck","Chest","Pelvis","Foot"} local d=Drawing.new("Circle")d.Color=Color3.new(1,1,1)d.Thickness=1 d.NumSides=100 d.Radius=F d.Transparency=1 d.Filled=false d.Visible=true local conns={} local function cycle()local i=table.find(b,B)B=b[(i%#b)+1]warn("AimBone changed to",B)print("AimBone changed to",B)end conns[#conns+1]=L.MessageOut:Connect(function(m,t)if t==Enum.MessageType.MessageOutput then local cmd,val=m:match("^(%S+)%s*(%S*)$") if cmd=="SetFov"and tonumber(val)then F=tonumber(val) d.Radius=F warn("Fov successfully set to",F) print("Fov successfully set to",F) elseif cmd=="AimStrength"and tonumber(val)then A=math.clamp(tonumber(val),0,1) warn("AimStrength set to",A) print("AimStrength set to",A) elseif cmd=="Aimbonechange"then cycle() elseif m=="STOPRUNNINGMIIIIAAIM"then warn("Aimbot script stopped") print("Aimbot script stopped") for _,c in pairs(conns)do if c.Disconnect then c:Disconnect()end end if d then d:Remove()end if loop then loop:Disconnect()end end end end) local function getClosest() local c,s=nil,F for _,pl in pairs(P:GetPlayers())do if pl~=P.LocalPlayer and pl.Team~=P.LocalPlayer.Team then local ch=pl.Character local bon=ch and ch:FindFirstChild(B) local hum=ch and ch:FindFirstChild("Humanoid") if bon and hum and hum.Health>0 then local pos,on=C:WorldToViewportPoint(bon.Position) if on then local center=Vector2.new(C.ViewportSize.X/2,C.ViewportSize.Y/2) local dist=(Vector2.new(pos.X,pos.Y)-center).Magnitude if dist<s then s=dist c=bon end end end end end return c end loop=S.RenderStepped:Connect(function() local center=Vector2.new(C.ViewportSize.X/2,C.ViewportSize.Y/2)d.Position=center d.Radius=F if U:IsMouseButtonPressed(Enum.UserInputType.MouseButton2)then local t=getClosest() if t then local dir=(t.Position-C.CFrame.Position).Unit local goal=CFrame.new(C.CFrame.Position,C.CFrame.Position+dir) C.CFrame=C.CFrame:Lerp(goal,A)end end end)
