local Players,RunService,LogService,LocalPlayer=game:GetService("Players"),game:GetService("RunService"),game:GetService("LogService"),game:GetService("Players").LocalPlayer;local active=true;LogService.MessageOut:Connect(function(m)if m:find("StopNameEsp")then for _,p in pairs(Players:GetPlayers())do if p.Character then local r=p.Character:FindFirstChild("HumanoidRootPart")if r then local g=r:FindFirstChild("NameLabel")if g then g:Destroy()end end end end active=false end end);RunService.RenderStepped:Connect(function()if not active then return end for _,p in pairs(Players:GetPlayers())do if p~=LocalPlayer then pcall(function()local c=p.Character;if not c then return end local hrp=c:FindFirstChild("HumanoidRootPart")if not hrp then return end local gui=hrp:FindFirstChild("NameLabel")if p.Team==LocalPlayer.Team then if gui then gui:Destroy()end return end if not gui then local b=Instance.new("BillboardGui")b.Name="NameLabel"b.Adornee=hrp b.Size=UDim2.new(0,120,0,18) b.StudsOffset=Vector3.new(0,3,0) b.AlwaysOnTop=true local l=Instance.new("TextLabel")l.Name="TextLabel"l.Parent=b l.Size=UDim2.new(1,0,1,0) l.BackgroundTransparency=1 l.TextStrokeTransparency=0 l.Font=Enum.Font.SourceSansBold l.TextScaled=true b.Parent=hrp gui=b end local label=gui.TextLabel local dist=(hrp.Position - LocalPlayer.Character.HumanoidRootPart.Position).Magnitude local brightness=math.clamp(1-(dist/100),0.3,1) local baseColor=Color3.fromRGB(170,0,255) label.Text="| "..p.Name.." |" label.TextColor3=Color3.new(baseColor.R*brightness,baseColor.G*brightness,baseColor.B*brightness)end)end end end)
